    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 shadow-lg z-50">
      <div className="flex items-center justify-between px-4 py-3 max-w-6xl mx-auto">

        {/* Track Info */}
        <div className="flex items-center space-x-3 flex-1 min-w-0">
          <div className="w-12 h-12 bg-gray-200 rounded flex items-center justify-center flex-shrink-0">
            <span className="text-gray-500 text-xs">🎵</span>
          </div>
          <div className="min-w-0">
            {currentTrack ? (
              <>
                <div className="font-medium text-gray-900 truncate">{currentTrack.title}</div>
                <div className="text-sm text-gray-500 truncate">
                  {currentTrack.artist || 'Unknown Artist'}
                </div>
              </>
            ) : (
              <>
                <div className="font-medium text-gray-400 truncate">No track selected</div>
                <div className="text-sm text-gray-400 truncate">
                  Choose a track to start playing
                </div>
              </>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-col items-center space-y-2 flex-2">
          {/* Control Buttons */}
          <div className="flex items-center space-x-4">
            <button
              onClick={playPrevious}
              disabled={!currentTrack}
              className="p-2 rounded-full hover:bg-gray-100 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              title="Previous track"
            >
              <SkipBack className={`w-5 h-5 ${currentTrack ? 'text-gray-600' : 'text-gray-400'}`} />
            </button>

            <button
              onClick={currentTrack ? (isPlaying ? pause : resume) : undefined}
              disabled={!currentTrack}
              className={`p-3 rounded-full transition-colors ${
                currentTrack 
                  ? 'bg-blue-600 hover:bg-blue-700' 
                  : 'bg-gray-400 cursor-not-allowed'
              }`}
              title={currentTrack ? (isPlaying ? 'Pause' : 'Play') : 'No track selected'}
            >
              {isPlaying ? (
                <Pause className="w-6 h-6 text-white" />
              ) : (
                <Play className="w-6 h-6 text-white ml-0.5" />
              )}
            </button>

            <button
              onClick={playNext}
              disabled={!currentTrack}
              className="p-2 rounded-full hover:bg-gray-100 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              title="Next track"
            >
              <SkipForward className={`w-5 h-5 ${currentTrack ? 'text-gray-600' : 'text-gray-400'}`} />
            </button>
          </div>

          {/* Progress Bar */}
          <div className="flex items-center space-x-2 w-full max-w-md">
            <span className="text-xs text-gray-500 w-10 text-right">
              {currentTrack ? formatTime(currentTime) : '--:--'}
            </span>
            <div className="flex-1">
              <input
                type="range"
                min="0"
                max={duration || 0}
                value={currentTime || 0}
                onChange={handleSeek}
                disabled={!currentTrack}
                className="w-full h-1 bg-gray-200 rounded-lg appearance-none cursor-pointer slider disabled:cursor-not-allowed disabled:opacity-50"
                style={{
                  background: currentTrack ? `linear-gradient(to right, #3b82f6 0%, #3b82f6 ${
                    duration ? (currentTime / duration) * 100 : 0
                  }%, #e5e7eb ${
                    duration ? (currentTime / duration) * 100 : 0
                  }%, #e5e7eb 100%)` : '#e5e7eb'
                }}
              />
            </div>
            <span className="text-xs text-gray-500 w-10">
              {currentTrack ? formatTime(duration) : '--:--'}
            </span>
          </div>
        </div>

        {/* Volume */}
        <div className="flex items-center space-x-2 flex-1 justify-end">
          <Volume2 className="w-4 h-4 text-gray-500" />
          <input
            type="range"
            min="0"
            max="1"
            step="0.1"
            value={volume}
            onChange={handleVolumeChange}
            className="w-20 h-1 bg-gray-200 rounded-lg appearance-none cursor-pointer"
            style={{
              background: `linear-gradient(to right, #3b82f6 0%, #3b82f6 ${
                volume * 100
              }%, #e5e7eb ${volume * 100}%, #e5e7eb 100%)`
            }}
          />
        </div>
      </div>

      <style jsx>{`
        .slider::-webkit-slider-thumb {
          appearance: none;
          width: 16px;
