import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { playlistQuerySchema, createPlaylistSchema } from '@/lib/validation/playlists'

// GET /api/playlists - List user's playlists
export async function GET(request: NextRequest) {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Validate query parameters
    const { searchParams } = new URL(request.url)
    const queryResult = playlistQuerySchema.safeParse({
      include_public: searchParams.get('include_public'),
      with_tracks: searchParams.get('with_tracks'),
      limit: searchParams.get('limit'),
      offset: searchParams.get('offset')
    })

    if (!queryResult.success) {
      return NextResponse.json({ 
        error: 'Invalid query parameters',
        details: queryResult.error.issues
      }, { status: 400 })
    }

    const { include_public: includePublic, with_tracks: withTracks } = queryResult.data

    let query = supabase
      .from('playlists')
      .select('*')
      .is('deleted_at', null)
      .order('updated_at', { ascending: false })

    // Base query: user's own playlists
    if (includePublic) {
      // Get own + public playlists
      query = query.or(`created_by.eq.${user.id},is_public.eq.true`)
    } else {
      // Get only own playlists
      query = query.eq('created_by', user.id)
    }

    const { data: playlists, error } = await query

    if (error) {
      console.error('Database error:', error)
      return NextResponse.json({ error: 'Failed to fetch playlists' }, { status: 500 })
    }

    // Enhance playlists with track count and duration data using a single aggregated query
    if (playlists && playlists.length > 0) {
      const playlistIds = playlists.map(p => p.id)

      // Get all track stats for all playlists in one query
      const { data: trackStats } = await supabase
        .from('playlist_tracks')
        .select(`
          playlist_id,
          tracks!inner(duration_seconds)
        `)
        .in('playlist_id', playlistIds)
        .is('tracks.deleted_at', null)

      // Group track stats by playlist ID
      const statsByPlaylist = (trackStats || []).reduce((acc, stat) => {
        const playlistId = stat.playlist_id
        if (!acc[playlistId]) {
          acc[playlistId] = { count: 0, totalDuration: 0 }
        }
        acc[playlistId].count += 1
        acc[playlistId].totalDuration += stat.tracks.duration_seconds || 0
        return acc
      }, {} as Record<string, { count: number; totalDuration: number }>)

      // If withTracks is true, fetch full track data for each playlist
      let enhancedPlaylists
      if (withTracks) {
        const playlistsWithTracks = await Promise.all(playlists.map(async (playlist) => {
          const { data: playlistTracks, error: playlistTracksError } = await supabase
            .from('playlist_tracks')
            .select(`
              id,
              position,
              created_at,
              track:tracks!inner(*)
            `)
            .eq('playlist_id', playlist.id)
            .is('tracks.deleted_at', null)
            .order('position', { ascending: true })

          if (playlistTracksError) {
            console.error('Failed to fetch playlist tracks:', playlistTracksError)
            // Return playlist without tracks if fetch fails
            return {
              ...playlist,
              track_count: 0,
              total_duration: 0,
              tracks: []
            }
          }

          // With inner join, all tracks should be valid
          const validTracks = playlistTracks || []

          return {
            ...playlist,
            track_count: validTracks.length,
            total_duration: validTracks.reduce((sum, pt) => sum + (pt.track?.duration_seconds || 0), 0),
            tracks: validTracks
          }
        }))
        enhancedPlaylists = playlistsWithTracks
      } else {
        // Merge stats with playlists (no tracks)
        enhancedPlaylists = playlists.map(playlist => ({
          ...playlist,
          track_count: statsByPlaylist[playlist.id]?.count || 0,
          total_duration: statsByPlaylist[playlist.id]?.totalDuration || 0
        }))
      }

      return NextResponse.json({
        success: true,
        playlists: enhancedPlaylists
      })
    }

    const enhancedPlaylists = playlists || []

    return NextResponse.json({
      success: true,
      playlists: enhancedPlaylists || []
    })

  } catch (error) {
    console.error('Playlists get error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

// POST /api/playlists - Create new playlist
export async function POST(request: NextRequest) {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const body = await request.json()

    // Validate request body
    const bodyResult = createPlaylistSchema.safeParse(body)

    if (!bodyResult.success) {
      return NextResponse.json({ 
        error: 'Invalid request data',
        details: bodyResult.error.issues
      }, { status: 400 })
    }

    const { title, description, is_public, is_collaborative } = bodyResult.data

    // Create playlist
    const playlistData = {
      title, // Already trimmed by schema
      description, // Already processed by schema
      created_by: user.id,
      is_public,
      is_collaborative,
    }

    const { data: playlist, error } = await supabase
      .from('playlists')
      .insert(playlistData)
      .select()
      .single()

    if (error) {
      console.error('Database error:', error)
      return NextResponse.json({ error: 'Failed to create playlist' }, { status: 500 })
    }

    return NextResponse.json({
      success: true,
      playlist: {
        ...playlist,
        track_count: 0,
        total_duration: 0
      }
    }, { status: 201 })

  } catch (error) {
    console.error('Playlist creation error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { validateTrackSearch } from '@/lib/api/validation'

export async function GET(request: NextRequest) {
  try {
    // Check authentication
    const supabase = await createClient()
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }

    // Validate search parameters
    const searchParams = validateTrackSearch(request)

    let query = supabase
      .from('tracks')
      .select('*', { count: 'exact' })
      .eq('created_by', user.id)
      .is('deleted_at', null)

    // Apply search filters
    if (searchParams.query) {
      // Enhanced search: title, artist, and file name
      const searchTerm = searchParams.query.trim()
      query = query.or(`title.ilike.%${searchTerm}%, artist.ilike.%${searchTerm}%, file_name.ilike.%${searchTerm}%`)
    }

    if (searchParams.genre) {
      query = query.eq('genre', searchParams.genre)
    }

    if (searchParams.bpm_min) {
      query = query.gte('bpm', searchParams.bpm_min)
    }

    if (searchParams.bpm_max) {
      query = query.lte('bpm', searchParams.bpm_max)
    }

    if (searchParams.key) {
      query = query.eq('key_signature', searchParams.key)
    }

    if (searchParams.date_from) {
      query = query.gte('created_at', searchParams.date_from)
    }

    if (searchParams.date_to) {
      // Add one day to include the entire end date
      const endDate = new Date(searchParams.date_to)
      endDate.setDate(endDate.getDate() + 1)
      query = query.lt('created_at', endDate.toISOString())
    }

    // Apply sorting
    query = query.order(searchParams.sort_by!, { 
      ascending: searchParams.sort_order === 'asc' 
    })

    // Apply pagination only if search/filter is applied or explicitly requested
    if (searchParams.query || searchParams.genre || searchParams.bpm_min || searchParams.bpm_max || searchParams.key || searchParams.date_from || searchParams.date_to || (searchParams.page && searchParams.page > 1)) {
      const from = (searchParams.page! - 1) * searchParams.limit!
      const to = from + searchParams.limit! - 1
      query = query.range(from, to)
    }

    const { data: tracks, error, count } = await query

    if (error) {
      console.error('Error fetching tracks:', error)
      return NextResponse.json({ error: error.message }, { status: 500 })
    }

    // Return paginated response if search/filter applied or pagination was requested
    if (searchParams.query || searchParams.genre || searchParams.bpm_min || searchParams.bpm_max || searchParams.key || searchParams.date_from || searchParams.date_to || (searchParams.page && searchParams.page > 1)) {
      return NextResponse.json({
        success: true,
        data: tracks || [],
        pagination: {
          total: count || 0,
          page: searchParams.page!,
          limit: searchParams.limit!,
          pages: Math.ceil((count || 0) / searchParams.limit!)
        }
      })
    }

    // For simple "load all tracks" request, return all tracks with total count
    return NextResponse.json({ 
      success: true, 
      data: {
        tracks: tracks || [],
        count: count || 0  // Use the actual database count, not just array length
      }
    })

  } catch (error) {
    console.error('Server error:', error)
    return NextResponse.json({ error: 'Server error' }, { status: 500 })
  }
}

// Handle OPTIONS for CORS
export function OPTIONS() {
  return new NextResponse(null, {
    status: 200,
    headers: {
      'Access-Control-Allow-Origin': '*',
      'Access-Control-Allow-Methods': 'GET, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type, Authorization, Range',
    },
  })
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { b2Service } from '@/lib/storage/backblaze'
import { 
  handleOptions,
  AuthenticatedRequest,
  ApiResponse
} from '@/lib/api/middleware'
import { validateTrackUpload } from '@/lib/api/validation'

async function uploadTrackHandler(request: AuthenticatedRequest) {
  const supabase = await createClient()
  const { user } = request

  if (!user) {
    throw new Error('User not found in authenticated request')
  }

  // Validation is handled by middleware
  const uploadData = await validateTrackUpload(request)
  const { file, title, artist, description, genre, bpm, keySignature, duration, folderMetadata } = uploadData

  // Convert File to Buffer for upload
  console.log('📁 Converting file to buffer:', { fileName: file.name, fileSize: file.size, fileType: file.type })
  const arrayBuffer = await file.arrayBuffer()
  const buffer = Buffer.from(arrayBuffer)
  console.log('✅ Buffer created successfully:', { bufferSize: buffer.length })

  // Upload to Backblaze B2
  console.log('🚀 Starting Backblaze upload...')
  let uploadResult
  try {
    uploadResult = await b2Service.uploadFile(
      buffer,
      file.name,
      file.type,
      {
        'uploaded_by': user!.id,
        'original_name': file.name,
        'title': title,
        'genre': genre || '',
        'bpm': bpm?.toString() || '',
        'key_signature': keySignature || '',
      }
    )
    console.log('✅ Backblaze upload successful:', uploadResult)
  } catch (uploadError) {
    console.error('❌ Backblaze upload failed:', uploadError)
    throw new Error(`Failed to upload file to storage: ${uploadError instanceof Error ? uploadError.message : 'Unknown error'}`)
  }

  // Create track record in database
  const trackData = {
    title,
    artist,
    description,
    file_name: file.name,
    file_size: file.size,
    file_type: file.type,
    storage_url: uploadResult.fileUrl,
    storage_key: uploadResult.fileName,
    file_url: uploadResult.fileUrl, // For backwards compatibility
    user_id: user!.id, // For backwards compatibility
    created_by: user!.id,
    is_public: false, // All tracks private for band collaboration
    genre,
    category: 'demo' as const,
    bpm,
    key_signature: keySignature,
    duration_seconds: duration,
    // Add folder metadata
    folder_path: folderMetadata?.folderPath || null,
    project_name: folderMetadata?.projectName || null,
    session_date: folderMetadata?.sessionDate || null,
    content_type: folderMetadata?.contentType || null,
    relative_path: folderMetadata?.relativePath || null,
    metadata: {
      original_name: file.name,
      uploaded_via: 'web_upload'
    }
  }

  const { data: track, error: dbError } = await supabase
    .from('tracks')
    .insert(trackData)
    .select()
    .single()

  if (dbError) {
    console.error('Database error:', dbError)

    // Clean up uploaded file if database insert fails
    try {
      await b2Service.deleteFile(uploadResult.fileName, uploadResult.fileId)
    } catch (cleanupError) {
      console.error('Failed to clean up uploaded file:', cleanupError)
    }

    throw new Error('Failed to save track information')
  }

  // Return success response
  return ApiResponse.success({
    track,
    upload: {
      fileId: uploadResult.fileId,
      fileName: uploadResult.fileName,
      fileUrl: uploadResult.fileUrl,
    },
  }, 201)
}

// Export handler with inline auth (following existing pattern from playlists route)
export async function POST(request: NextRequest) {
  try {
    const supabase = await createClient()

    // Check authentication - following existing pattern from playlists route
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Create authenticated request object
    const authRequest = request as AuthenticatedRequest
    authRequest.user = user

    return await uploadTrackHandler(authRequest)
  } catch (error) {
    console.error('Upload error:', error)
    if (error instanceof Error && error.message.includes('Failed to save track information')) {
      return NextResponse.json({ error: 'Database error saving track' }, { status: 500 })
    }
