import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { playlistQuerySchema, createPlaylistSchema } from '@/lib/validation/playlists'

// GET /api/playlists - List user's playlists
export async function GET(request: NextRequest) {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Validate query parameters
    const { searchParams } = new URL(request.url)
    const queryResult = playlistQuerySchema.safeParse({
      include_public: searchParams.get('include_public'),
      with_tracks: searchParams.get('with_tracks'),
      limit: searchParams.get('limit'),
      offset: searchParams.get('offset')
    })

    if (!queryResult.success) {
      return NextResponse.json({ 
        error: 'Invalid query parameters',
        details: queryResult.error.issues
      }, { status: 400 })
    }

    const { include_public: includePublic, with_tracks: withTracks } = queryResult.data

    // Fetch playlists using safe query methods to avoid SQL injection
    let playlists: any[] = []
    let error: any = null

    if (includePublic) {
      // Get own + public playlists - use separate queries to avoid SQL injection
      const [userPlaylistsResult, publicPlaylistsResult] = await Promise.all([
        supabase
          .from('playlists')
          .select('*')
          .eq('created_by', user.id)
          .is('deleted_at', null)
          .order('updated_at', { ascending: false }),
        supabase
          .from('playlists')
          .select('*')
          .eq('is_public', true)
          .neq('created_by', user.id) // Avoid duplicates
          .is('deleted_at', null)
          .order('updated_at', { ascending: false })
      ])

      if (userPlaylistsResult.error || publicPlaylistsResult.error) {
        error = userPlaylistsResult.error || publicPlaylistsResult.error
      } else {
        const userPlaylists = userPlaylistsResult.data || []
        const publicPlaylists = publicPlaylistsResult.data || []
        playlists = [...userPlaylists, ...publicPlaylists]
          .sort((a, b) => new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime())
      }
    } else {
      // Get only own playlists
      const result = await supabase
        .from('playlists')
        .select('*')
        .eq('created_by', user.id)
        .is('deleted_at', null)
        .order('updated_at', { ascending: false })

      playlists = result.data || []
      error = result.error
    }

    if (error) {
      console.error('Database error:', error)
      return NextResponse.json({ error: 'Failed to fetch playlists' }, { status: 500 })
    }

    // Enhance playlists with track count and duration data using a single aggregated query
    if (playlists && playlists.length > 0) {
      const playlistIds = playlists.map(p => p.id)

      // Get all track stats for all playlists in one query
      const { data: trackStats } = await supabase
        .from('playlist_tracks')
        .select(`
          playlist_id,
          tracks!inner(duration_seconds)
        `)
        .in('playlist_id', playlistIds)
        .is('tracks.deleted_at', null)

      // Group track stats by playlist ID
      const statsByPlaylist = (trackStats || []).reduce((acc, stat) => {
        const playlistId = stat.playlist_id
        if (!acc[playlistId]) {
          acc[playlistId] = { count: 0, totalDuration: 0 }
        }
        acc[playlistId].count += 1
        acc[playlistId].totalDuration += stat.tracks.duration_seconds || 0
        return acc
      }, {} as Record<string, { count: number; totalDuration: number }>)

      // If withTracks is true, fetch full track data for each playlist
      let enhancedPlaylists
      if (withTracks) {
        const playlistsWithTracks = await Promise.all(playlists.map(async (playlist) => {
          const { data: playlistTracks, error: playlistTracksError } = await supabase
            .from('playlist_tracks')
            .select(`
              id,
              position,
              created_at,
              track:tracks!inner(*)
            `)
            .eq('playlist_id', playlist.id)
            .is('tracks.deleted_at', null)
            .order('position', { ascending: true })

          if (playlistTracksError) {
            console.error('Failed to fetch playlist tracks:', playlistTracksError)
            // Return playlist without tracks if fetch fails
            return {
              ...playlist,
              track_count: 0,
              total_duration: 0,
              tracks: []
            }
          }

          // With inner join, all tracks should be valid
          const validTracks = playlistTracks || []

          return {
            ...playlist,
            track_count: validTracks.length,
            total_duration: validTracks.reduce((sum, pt) => sum + (pt.track?.duration_seconds || 0), 0),
            tracks: validTracks
          }
        }))
        enhancedPlaylists = playlistsWithTracks
      } else {
        // Merge stats with playlists (no tracks)
        enhancedPlaylists = playlists.map(playlist => ({
          ...playlist,
          track_count: statsByPlaylist[playlist.id]?.count || 0,
          total_duration: statsByPlaylist[playlist.id]?.totalDuration || 0
        }))
      }

      return NextResponse.json({
        success: true,
        playlists: enhancedPlaylists
      })
    }

    const enhancedPlaylists = playlists || []

    return NextResponse.json({
      success: true,
      playlists: enhancedPlaylists || []
    })

  } catch (error) {
    console.error('Playlists get error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

// POST /api/playlists - Create new playlist
export async function POST(request: NextRequest) {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    const body = await request.json()

    // Validate request body
    const bodyResult = createPlaylistSchema.safeParse(body)

    if (!bodyResult.success) {
      return NextResponse.json({ 
        error: 'Invalid request data',
        details: bodyResult.error.issues
      }, { status: 400 })
    }

    const { title, description, is_public, is_collaborative } = bodyResult.data

    // Create playlist
    const playlistData = {
      title, // Already trimmed by schema
      description, // Already processed by schema
      created_by: user.id,
      is_public,
      is_collaborative,
    }

    const { data: playlist, error } = await supabase
      .from('playlists')
      .insert(playlistData)
      .select()
      .single()

    if (error) {
      console.error('Database error:', error)
      return NextResponse.json({ error: 'Failed to create playlist' }, { status: 500 })
    }

    return NextResponse.json({
      success: true,
      playlist: {
        ...playlist,
        track_count: 0,
        total_duration: 0
      }
    }, { status: 201 })

  } catch (error) {
    console.error('Playlist creation error:', error)
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 })
  }
}

