      }
    }, 201)

  } catch (error) {
    console.error('Error in ensemble playlists POST:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

export async function DELETE(request: NextRequest, { params }: RouteParams) {
  try {
    const supabase = await createClient()
    const { data: { user }, error: userError } = await supabase.auth.getUser()

    if (userError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Await params for Next.js 15 compatibility
    const { id: ensembleId } = await params

    const { searchParams } = new URL(request.url)
    const playlist_id = searchParams.get('playlist_id')

    if (!playlist_id) {
      return NextResponse.json({ error: 'Playlist ID is required' }, { status: 400 })
    }

    // Check if user has permission to remove playlists from this ensemble
    const { data: userMembership, error: membershipError } = await supabase
      .from('ensemble_members')
      .select('role, status')
      .eq('ensemble_id', ensembleId)
      .eq('user_id', user.id)
      .eq('status', 'active')
      .single()

    const { data: ensemble, error: ensembleError } = await supabase
      .from('ensembles')
      .select('created_by, name')
      .eq('id', ensembleId)
      .is('deleted_at', null)
      .single()

    if (ensembleError) {
      if (ensembleError.code === 'PGRST116') {
        return NextResponse.json({ error: 'Ensemble not found' }, { status: 404 })
      }
      return createErrorResponse('Failed to check ensemble permissions', 500)
    }

    const isCreator = ensemble.created_by === user.id
    const isAdmin = userMembership?.role === 'admin'

    // Check if playlist belongs to the user or if they're ensemble creator/admin
    const { data: ensemblePlaylist, error: playlistCheckError } = await supabase
      .from('ensemble_playlists')
      .select(`
        id,
        added_by,
        playlists(id, title, created_by)
      `)
      .eq('ensemble_id', ensembleId)
      .eq('playlist_id', playlist_id)
      .single()

    if (playlistCheckError || !ensemblePlaylist) {
      return NextResponse.json({ error: 'Playlist not found in this ensemble' }, { status: 404 })
    }

    const canRemove = isCreator || 
                     isAdmin || 
                     ensemblePlaylist.playlists.created_by === user.id ||
                     ensemblePlaylist.added_by === user.id

    if (!canRemove) {
      return NextResponse.json({ error: 'Insufficient permissions to remove this playlist' }, { status: 403 })
    }

    // Remove playlist from ensemble
    const { error: removeError } = await supabase
      .from('ensemble_playlists')
      .delete()
      .eq('id', ensemblePlaylist.id)

    if (removeError) {
      console.error('Error removing playlist from ensemble:', removeError)
      return createErrorResponse('Failed to remove playlist from ensemble', 500)
    }

    // Log the activity
    await supabase
      .from('ensemble_activity')
      .insert({
        ensemble_id: ensembleId,
        user_id: user.id,
        action: 'playlist_removed',
        resource_type: 'playlist',
        resource_id: ensemblePlaylist.id,
        metadata: { 
          playlist_name: ensemblePlaylist.playlists.title,
          playlist_id
        }
      })

    return createSuccessResponse({ data: { success: true } })

  } catch (error) {
    console.error('Error in ensemble playlists DELETE:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

-- Ensemble Playlist Copy Model Migration
-- Changes the architecture from referencing original playlists to copying them
-- This ensures privacy, security, and proper collaboration boundaries

BEGIN;

-- Add fields to playlists table to support ensemble copies and track lineage
ALTER TABLE playlists ADD COLUMN IF NOT EXISTS source_playlist_id UUID REFERENCES playlists(id) ON DELETE SET NULL;
ALTER TABLE playlists ADD COLUMN IF NOT EXISTS is_ensemble_copy BOOLEAN DEFAULT false;
ALTER TABLE playlists ADD COLUMN IF NOT EXISTS ensemble_context_id UUID REFERENCES ensembles(id) ON DELETE CASCADE;

-- Add index for performance when finding ensemble playlists
CREATE INDEX IF NOT EXISTS idx_playlists_ensemble_copies ON playlists(is_ensemble_copy, ensemble_context_id) WHERE is_ensemble_copy = true;
CREATE INDEX IF NOT EXISTS idx_playlists_source ON playlists(source_playlist_id) WHERE source_playlist_id IS NOT NULL;

-- Add comment to clarify the new architecture
COMMENT ON COLUMN playlists.source_playlist_id IS 'References the original playlist this was copied from (for ensemble copies)';
COMMENT ON COLUMN playlists.is_ensemble_copy IS 'True if this playlist is a copy created for ensemble collaboration';
COMMENT ON COLUMN playlists.ensemble_context_id IS 'The ensemble this playlist belongs to (for ensemble copies only)';

-- Update RLS policies to handle ensemble copies
-- Ensemble copy playlists should be accessible to ensemble members

-- Drop existing policies that might conflict
DROP POLICY IF EXISTS "Ensemble members can view ensemble playlists" ON playlists;

-- Create new policy for ensemble playlist access
CREATE POLICY "Ensemble members can view ensemble playlists" ON playlists
  FOR SELECT USING (
    is_ensemble_copy = true 
    AND ensemble_context_id IN (
      -- User is a member of the ensemble
      SELECT e.id FROM ensembles e 
      WHERE e.id = ensemble_context_id
        AND (
          e.created_by = auth.uid() 
          OR EXISTS (
            SELECT 1 FROM ensemble_members em 
            WHERE em.ensemble_id = e.id 
              AND em.user_id = auth.uid() 
              AND em.status = 'active'
          )
        )
    )
  );

-- Ensemble members can edit ensemble playlists (if collaborative)
CREATE POLICY "Ensemble members can edit ensemble playlists" ON playlists
  FOR UPDATE USING (
    is_ensemble_copy = true 
    AND is_collaborative = true
    AND ensemble_context_id IN (
      -- User is a member of the ensemble
      SELECT e.id FROM ensembles e 
      WHERE e.id = ensemble_context_id
        AND (
          e.created_by = auth.uid() 
          OR EXISTS (
            SELECT 1 FROM ensemble_members em 
            WHERE em.ensemble_id = e.id 
              AND em.user_id = auth.uid() 
              AND em.status = 'active'
          )
        )
    )
  );

-- Add function to copy playlist with all its tracks
CREATE OR REPLACE FUNCTION copy_playlist_to_ensemble(
  original_playlist_id UUID,
  target_ensemble_id UUID,
  copying_user_id UUID
) RETURNS UUID AS $$
DECLARE
  original_playlist RECORD;
  new_playlist_id UUID;
  track_record RECORD;
BEGIN
  -- Get the original playlist details
  SELECT * INTO original_playlist 
  FROM playlists 
  WHERE id = original_playlist_id 
    AND (created_by = copying_user_id OR is_public = true)
    AND deleted_at IS NULL;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'Original playlist not found or access denied';
  END IF;

  -- Create the copied playlist
  INSERT INTO playlists (
    title,
    description,
    created_by,
    is_public,
    is_collaborative,
    source_playlist_id,
    is_ensemble_copy,
    ensemble_context_id
  ) VALUES (
    original_playlist.title, -- Keep same title
    original_playlist.description,
    copying_user_id, -- Set copying user as creator
    false, -- Ensemble copies are not public (accessible via ensemble membership)
    true, -- Ensemble playlists are collaborative by default
    original_playlist_id,
    true,
    target_ensemble_id
  ) RETURNING id INTO new_playlist_id;

  -- Copy all tracks with their positions
  INSERT INTO playlist_tracks (playlist_id, track_id, position, added_by)
  SELECT 
    new_playlist_id,
    pt.track_id,
    pt.position,
    copying_user_id
  FROM playlist_tracks pt
  WHERE pt.playlist_id = original_playlist_id
  ORDER BY pt.position;

  RETURN new_playlist_id;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Grant execute permission to authenticated users
GRANT EXECUTE ON FUNCTION copy_playlist_to_ensemble(UUID, UUID, UUID) TO authenticated;

COMMIT;

'use client'

import { useState, useEffect } from 'react'
import { X, Search, Music, Plus } from 'lucide-react'
import { PlaylistWithTracks } from '@/types/playlists'

interface AddPlaylistToEnsembleModalProps {
  isOpen: boolean
  onClose: () => void
  ensembleId: string
  ensembleName: string
  onPlaylistAdded?: () => void
}

export default function AddPlaylistToEnsembleModal({ 
  isOpen, 
  onClose, 
  ensembleId, 
  ensembleName,
  onPlaylistAdded 
}: AddPlaylistToEnsembleModalProps) {
  const [availablePlaylists, setAvailablePlaylists] = useState<PlaylistWithTracks[]>([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState<string | null>(null)
  const [searchQuery, setSearchQuery] = useState('')
  const [addingPlaylistId, setAddingPlaylistId] = useState<string | null>(null)
  const [mode, setMode] = useState<'existing' | 'create'>('existing')
  const [newPlaylistTitle, setNewPlaylistTitle] = useState('')
  const [newPlaylistDescription, setNewPlaylistDescription] = useState('')
  const [newPlaylistIsPublic, setNewPlaylistIsPublic] = useState(false)
  const [creatingPlaylist, setCreatingPlaylist] = useState(false)

  useEffect(() => {
    if (isOpen) {
      fetchAvailablePlaylists()
    }
  }, [isOpen, ensembleId])

  const fetchAvailablePlaylists = async () => {
    try {
      setLoading(true)
      setError(null)

      // Fetch user's playlists
      const response = await fetch('/api/playlists')
      const result = await response.json()

      if (!response.ok) {
        throw new Error(result.error || 'Failed to fetch playlists')
      }

      // Filter out playlists that are already in ensembles
      // TODO: Add API endpoint to check which playlists are already in ensembles
      setAvailablePlaylists(result.data || [])
    } catch (err) {
      console.error('Error fetching playlists:', err)
      setError(err instanceof Error ? err.message : 'Failed to load playlists')
    } finally {
      setLoading(false)
    }
  }

  const handleCreateAndAddPlaylist = async () => {
    if (!newPlaylistTitle.trim()) {
      setError('Playlist title is required')
      return
    }

    try {
      setCreatingPlaylist(true)
      setError(null)

      // Create the playlist
      const createResponse = await fetch('/api/playlists', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          title: newPlaylistTitle.trim(),
          description: newPlaylistDescription.trim() || null,
          is_public: newPlaylistIsPublic,
          is_collaborative: false
        })
      })

      const createResult = await createResponse.json()

      if (!createResponse.ok) {
        throw new Error(createResult.error || 'Failed to create playlist')
      }

      const newPlaylist = createResult.playlist

      // Add the newly created playlist to the ensemble
      const addResponse = await fetch(`/api/ensembles/${ensembleId}/playlists`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ playlist_id: newPlaylist.id })
      })

      const addResult = await addResponse.json()

      if (!addResponse.ok) {
        throw new Error(addResult.error || 'Failed to add playlist to ensemble')
      }

      // Reset form
      setNewPlaylistTitle('')
      setNewPlaylistDescription('')
      setNewPlaylistIsPublic(false)

      if (onPlaylistAdded) {
        onPlaylistAdded()
      }

      // Close modal
      onClose()

    } catch (err) {
      console.error('Error creating and adding playlist:', err)
      setError(err instanceof Error ? err.message : 'Failed to create and add playlist')
    } finally {
      setCreatingPlaylist(false)
    }
  }

  const handleAddPlaylist = async (playlistId: string) => {
    try {
      setAddingPlaylistId(playlistId)
      setError(null)

      const response = await fetch(`/api/ensembles/${ensembleId}/playlists`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ playlist_id: playlistId })
      })

      const result = await response.json()

      if (!response.ok) {
        throw new Error(result.error || 'Failed to add playlist to ensemble')
      }

      // Remove the added playlist from available list
      setAvailablePlaylists(prev => prev.filter(p => p.id !== playlistId))

      if (onPlaylistAdded) {
        onPlaylistAdded()
      }

    } catch (err) {
      console.error('Error adding playlist to ensemble:', err)
      setError(err instanceof Error ? err.message : 'Failed to add playlist')
    } finally {
      setAddingPlaylistId(null)
    }
  }

  const filteredPlaylists = availablePlaylists.filter(playlist =>
    playlist.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    (playlist.description && playlist.description.toLowerCase().includes(searchQuery.toLowerCase()))
  )

  if (!isOpen) return null

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-md bg-white">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-medium text-gray-900">
            Copy Playlist to {ensembleName}
          </h3>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        {error && (
          <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
            <p className="text-red-800 text-sm">{error}</p>
          </div>
        )}

        {/* Information Banner */}
        <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded-md">
          <div className="flex items-start">
            <div className="flex-shrink-0">
              <Music className="h-5 w-5 text-blue-600 mt-0.5" />
            </div>
            <div className="ml-3">
              <h4 className="text-sm font-medium text-blue-800">Privacy & Collaboration</h4>
              <p className="text-sm text-blue-700 mt-1">
                Adding a playlist creates a copy for ensemble collaboration. Your original playlist remains private and unchanged.
              </p>
            </div>
          </div>
        </div>

        {/* Mode Toggle */}
        <div className="mb-4">
          <div className="flex bg-gray-100 rounded-lg p-1">
            <button
              onClick={() => setMode('existing')}
              className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                mode === 'existing'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              Copy Existing
            </button>
            <button
              onClick={() => setMode('create')}
              className={`flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                mode === 'create'
                  ? 'bg-white text-blue-600 shadow-sm'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              Create New
            </button>
          </div>
        </div>

        {mode === 'create' ? (
          /* Create New Playlist Form */
          <div className="space-y-4">
            <div>
              <label htmlFor="playlist-title" className="block text-sm font-medium text-gray-700 mb-1">
                Playlist Title *
              </label>
              <input
                id="playlist-title"
                type="text"
                value={newPlaylistTitle}
                onChange={(e) => setNewPlaylistTitle(e.target.value)}
                placeholder="Enter playlist title..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div>
              <label htmlFor="playlist-description" className="block text-sm font-medium text-gray-700 mb-1">
                Description (Optional)
              </label>
              <textarea
                id="playlist-description"
                value={newPlaylistDescription}
                onChange={(e) => setNewPlaylistDescription(e.target.value)}
                placeholder="Enter playlist description..."
                rows={3}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            <div className="flex items-center">
              <input
                id="playlist-public"
                type="checkbox"
                checked={newPlaylistIsPublic}
                onChange={(e) => setNewPlaylistIsPublic(e.target.checked)}
                className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
              />
              <label htmlFor="playlist-public" className="ml-2 text-sm text-gray-700">
                Make this playlist public
              </label>
            </div>

            <div className="flex justify-end space-x-3 pt-4">
              <button
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleCreateAndAddPlaylist}
                disabled={creatingPlaylist || !newPlaylistTitle.trim()}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {creatingPlaylist ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b border-white mr-2"></div>
                    Creating...
                  </>
                ) : (
                  <>
                    <Plus className="h-4 w-4 mr-2" />
                    Create & Add
                  </>
                )}
              </button>
            </div>
          </div>
        ) : (
          /* Add Existing Playlist */
          <div>
            {/* Search */}
            <div className="relative mb-4">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search your playlists..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Playlist List */}
            <div className="max-h-96 overflow-y-auto">
              {loading ? (
                <div className="text-center py-8">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="text-gray-500 mt-2">Loading playlists...</p>
                </div>
              ) : filteredPlaylists.length === 0 ? (
                <div className="text-center py-8">
                  <Music className="mx-auto h-12 w-12 text-gray-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900">
                    {searchQuery ? 'No matching playlists' : 'No available playlists'}
                  </h3>
                  <p className="mt-1 text-sm text-gray-500">
                    {searchQuery 
                      ? 'Try adjusting your search terms.'
                      : 'Create some playlists first, or all your playlists may already be in ensembles.'
                    }
                  </p>
                </div>
              ) : (
                <div className="space-y-2">
                  {filteredPlaylists.map((playlist) => (
                    <div
                      key={playlist.id}
                      className="flex items-center justify-between p-3 border border-gray-200 rounded-lg hover:bg-gray-50"
                    >
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">{playlist.title}</h4>
                        {playlist.description && (
                          <p className="text-sm text-gray-500 mt-1">{playlist.description}</p>
                        )}
                        <div className="flex items-center gap-4 mt-2 text-xs text-gray-500">
                          <span>{playlist.playlist_tracks?.length || 0} tracks</span>
                          <span>{playlist.is_public ? 'Public' : 'Private'}</span>
                          <span>Created {new Date(playlist.created_at).toLocaleDateString()}</span>
                        </div>
                      </div>
                      <button
                        onClick={() => handleAddPlaylist(playlist.id)}
                        disabled={addingPlaylistId === playlist.id}
                        className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded text-blue-600 bg-blue-50 hover:bg-blue-100 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        {addingPlaylistId === playlist.id ? (
                          <>
                            <div className="animate-spin rounded-full h-3 w-3 border-b border-blue-600 mr-2"></div>
                            Copying...
                          </>
                        ) : (
                          <>
                            <Plus className="h-3 w-3 mr-1" />
                            Copy
                          </>
                        )}
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="flex justify-end space-x-3 pt-4 mt-4 border-t">
              <button
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { createErrorResponse, createSuccessResponse } from '@/lib/api/response'

interface RouteParams {
  params: {
    id: string
  }
}

export async function GET(request: NextRequest, { params }: RouteParams) {
  try {
    const supabase = await createClient()
    const { data: { user }, error: userError } = await supabase.auth.getUser()

    if (userError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Await params for Next.js 15 compatibility
    const { id: ensembleId } = await params

    // Check if user has access to this ensemble
    const { data: userMembership, error: membershipError } = await supabase
      .from('ensemble_members')
      .select('role, status')
      .eq('ensemble_id', ensembleId)
      .eq('user_id', user.id)
      .eq('status', 'active')
      .single()

    const { data: ensemble, error: ensembleError } = await supabase
      .from('ensembles')
      .select('created_by')
      .eq('id', ensembleId)
      .is('deleted_at', null)
      .single()

    if (ensembleError || (!ensemble || (ensemble.created_by !== user.id && !userMembership))) {
      return NextResponse.json({ error: 'Access denied' }, { status: 403 })
    }

    // Get ensemble playlists with detailed information
    const { data: ensemblePlaylists, error: playlistsError } = await supabase
      .from('ensemble_playlists')
      .select(`
        id,
        added_by,
        added_at,
        playlists(
          id,
          title,
          description,
          created_at,
          created_by,
          is_public,
          playlist_tracks(count),
          profiles:created_by(full_name, avatar_url)
        )
      `)
      .eq('ensemble_id', ensembleId)
      .order('added_at', { ascending: false })

    if (playlistsError) {
      console.error('Error fetching ensemble playlists:', playlistsError)
      return createErrorResponse('Failed to fetch ensemble playlists', 500)
    }

    return createSuccessResponse({ data: ensemblePlaylists || [] })

  } catch (error) {
    console.error('Error in ensemble playlists GET:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

export async function POST(request: NextRequest, { params }: RouteParams) {
  try {
    const supabase = await createClient()
    const { data: { user }, error: userError } = await supabase.auth.getUser()

    if (userError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Await params for Next.js 15 compatibility
    const { id: ensembleId } = await params

    const body = await request.json()
    const { playlist_id } = body

    if (!playlist_id) {
      return NextResponse.json({ error: 'Playlist ID is required' }, { status: 400 })
    }

    // Check if user is a member of the ensemble
    const { data: userMembership, error: membershipError } = await supabase
      .from('ensemble_members')
      .select('role, status')
      .eq('ensemble_id', ensembleId)
      .eq('user_id', user.id)
      .eq('status', 'active')
      .single()

    const { data: ensemble, error: ensembleError } = await supabase
      .from('ensembles')
      .select('created_by, name')
      .eq('id', ensembleId)
      .is('deleted_at', null)
      .single()

    if (ensembleError) {
      if (ensembleError.code === 'PGRST116') {
        return NextResponse.json({ error: 'Ensemble not found' }, { status: 404 })
      }
      return createErrorResponse('Failed to check ensemble permissions', 500)
    }

    if (ensemble.created_by !== user.id && !userMembership) {
      return NextResponse.json({ error: 'Access denied' }, { status: 403 })
    }

    // Check if user owns the playlist
    const { data: playlist, error: playlistError } = await supabase
      .from('playlists')
      .select('id, title, created_by, deleted_at')
      .eq('id', playlist_id)
      .eq('created_by', user.id)
      .single()

    if (playlistError || !playlist) {
      return NextResponse.json({ error: 'Playlist not found or access denied' }, { status: 404 })
    }

    if (playlist.deleted_at) {
      return NextResponse.json({ error: 'Cannot add deleted playlist to ensemble' }, { status: 400 })
    }

    // Check if playlist is already in an ensemble
    const { data: existingEnsemblePlaylist, error: existingError } = await supabase
      .from('ensemble_playlists')
      .select('ensemble_id, ensembles(name)')
      .eq('playlist_id', playlist_id)
      .single()

    if (existingEnsemblePlaylist) {
      return NextResponse.json({ 
        error: `Playlist is already in ensemble "${existingEnsemblePlaylist.ensembles.name}"` 
      }, { status: 400 })
    }

    // Execute copy operation with transaction safety and comprehensive validation
    let copiedPlaylistId: string | null = null

    try {
      // Step 1: Additional validation checks
      const { data: existingPlaylistsCount, error: countError } = await supabase
        .from('ensemble_playlists')
        .select('id', { count: 'exact', head: true })
        .eq('ensemble_id', ensembleId)

      if (countError) {
        console.error('Error checking ensemble playlist count:', countError)
        return createErrorResponse('Failed to validate ensemble capacity', 500)
      }

      // Enforce reasonable limits (configurable in production)
      const MAX_ENSEMBLE_PLAYLISTS = 100
      if ((existingPlaylistsCount || 0) >= MAX_ENSEMBLE_PLAYLISTS) {
        return createErrorResponse(`Ensemble has reached maximum playlist limit (${MAX_ENSEMBLE_PLAYLISTS})`, 400)
      }

      // Step 2: Copy playlist using database function (atomic operation)
      const { data: copiedPlaylistData, error: copyError } = await supabase.rpc(
        'copy_playlist_to_ensemble',
        {
          original_playlist_id: playlist_id,
          target_ensemble_id: ensembleId,
          copying_user_id: user.id
        }
      )

      if (copyError) {
        console.error('Error copying playlist to ensemble:', copyError)
        // More specific error handling
        if (copyError.message?.includes('not found')) {
          return createErrorResponse('Original playlist not found or access denied', 404)
        }
        return createErrorResponse('Failed to copy playlist to ensemble', 500)
      }

      copiedPlaylistId = copiedPlaylistData as string

      if (!copiedPlaylistId) {
        throw new Error('Copy function returned null playlist ID')
      }

      // Step 3: Link copied playlist to ensemble with validation
      const { data: ensemblePlaylist, error: addError } = await supabase
        .from('ensemble_playlists')
        .insert({
          ensemble_id: ensembleId,
          playlist_id: copiedPlaylistId,
          added_by: user.id
        })
        .select(`
          id,
          added_by,
          added_at,
          playlists(
            id,
            title,
            description,
            created_at,
            created_by,
            is_public,
            source_playlist_id,
            is_ensemble_copy,
            playlist_tracks(count)
          )
        `)
        .single()

      if (addError) {
        console.error('Error linking copied playlist to ensemble:', addError)

        // CRITICAL: If linking fails, we need to clean up the orphaned copied playlist
        if (copiedPlaylistId) {
          console.log(`Cleaning up orphaned playlist copy: ${copiedPlaylistId}`)
          await supabase
            .from('playlists')
            .delete()
            .eq('id', copiedPlaylistId)
            .eq('is_ensemble_copy', true) // Safety check
        }

        return createErrorResponse('Failed to link copied playlist to ensemble', 500)
      }

      // Step 4: Log the activity (non-critical, don't fail the operation if this fails)
      const activityResult = await supabase
        .from('ensemble_activity')
        .insert({
          ensemble_id: ensembleId,
          user_id: user.id,
          action: 'playlist_copied',
          resource_type: 'playlist',
          resource_id: ensemblePlaylist.id,
          metadata: { 
            original_playlist_name: playlist.title,
            original_playlist_id: playlist_id,
            copied_playlist_id: copiedPlaylistId
          }
        })

      if (activityResult.error) {
        console.warn('Failed to log ensemble activity (non-critical):', activityResult.error)
      }

      // Step 5: Return success response
      return createSuccessResponse({ 
        data: {
          ...ensemblePlaylist,
          message: `Created a copy of "${playlist.title}" for ensemble collaboration. Your original playlist remains private.`
        }
      }, 201)

    } catch (operationError) {
      console.error('Critical error during playlist copy operation:', operationError)

      // Emergency cleanup: Remove any partially created resources
      if (copiedPlaylistId) {
        console.log(`Emergency cleanup: removing orphaned playlist ${copiedPlaylistId}`)
        try {
          await supabase
            .from('playlists')
            .delete()
            .eq('id', copiedPlaylistId)
            .eq('is_ensemble_copy', true) // Safety check
        } catch (cleanupError) {
          console.error('Failed to cleanup orphaned playlist:', cleanupError)
          // Log this for manual cleanup but don't fail the user request
        }
      }

      return createErrorResponse('Playlist copy operation failed. No changes were made.', 500)
    }

  } catch (error) {
    console.error('Error in ensemble playlists POST:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

export async function DELETE(request: NextRequest, { params }: RouteParams) {
  try {
    const supabase = await createClient()
    const { data: { user }, error: userError } = await supabase.auth.getUser()

    if (userError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    // Await params for Next.js 15 compatibility
    const { id: ensembleId } = await params

    const { searchParams } = new URL(request.url)
    const playlist_id = searchParams.get('playlist_id')

    if (!playlist_id) {
      return NextResponse.json({ error: 'Playlist ID is required' }, { status: 400 })
    }

    // Check if user has permission to remove playlists from this ensemble
    const { data: userMembership, error: membershipError } = await supabase
      .from('ensemble_members')
      .select('role, status')
      .eq('ensemble_id', ensembleId)
      .eq('user_id', user.id)
      .eq('status', 'active')
      .single()

    const { data: ensemble, error: ensembleError } = await supabase
      .from('ensembles')
      .select('created_by, name')
      .eq('id', ensembleId)
      .is('deleted_at', null)
      .single()

    if (ensembleError) {
      if (ensembleError.code === 'PGRST116') {
        return NextResponse.json({ error: 'Ensemble not found' }, { status: 404 })
      }
      return createErrorResponse('Failed to check ensemble permissions', 500)
    }

    const isCreator = ensemble.created_by === user.id
    const isAdmin = userMembership?.role === 'admin'

    // Check if playlist belongs to the user or if they're ensemble creator/admin
    const { data: ensemblePlaylist, error: playlistCheckError } = await supabase
      .from('ensemble_playlists')
      .select(`
        id,
        added_by,
        playlists(id, title, created_by)
      `)
      .eq('ensemble_id', ensembleId)
      .eq('playlist_id', playlist_id)
      .single()

    if (playlistCheckError || !ensemblePlaylist) {
      return NextResponse.json({ error: 'Playlist not found in this ensemble' }, { status: 404 })
    }

    const canRemove = isCreator || 
                     isAdmin || 
                     ensemblePlaylist.playlists.created_by === user.id ||
                     ensemblePlaylist.added_by === user.id

    if (!canRemove) {
      return NextResponse.json({ error: 'Insufficient permissions to remove this playlist' }, { status: 403 })
    }

    // Remove playlist from ensemble
    const { error: removeError } = await supabase
      .from('ensemble_playlists')
      .delete()
      .eq('id', ensemblePlaylist.id)

    if (removeError) {
      console.error('Error removing playlist from ensemble:', removeError)
      return createErrorResponse('Failed to remove playlist from ensemble', 500)
    }

    // Log the activity
    await supabase
      .from('ensemble_activity')
      .insert({
        ensemble_id: ensembleId,
        user_id: user.id,
        action: 'playlist_removed',
        resource_type: 'playlist',
        resource_id: ensemblePlaylist.id,
        metadata: { 
          playlist_name: ensemblePlaylist.playlists.title,
          playlist_id
        }
      })

    return createSuccessResponse({ data: { success: true } })

  } catch (error) {
    console.error('Error in ensemble playlists DELETE:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { playlistQuerySchema, createPlaylistSchema } from '@/lib/validation/playlists'
import { validatePaginationParams, createPaginatedResponse } from '@/lib/api/pagination'
import { createErrorResponse } from '@/lib/api/response'
import { 
  PlaylistBase, 
  PlaylistWithStats, 
  PlaylistWithTracks, 
  PlaylistTrackWithTrack,
  PlaylistVariant,
  CreatePlaylistRequest,
  PlaylistError 
} from '@/types/playlists'

// GET /api/playlists - List user's playlists
export async function GET(request: NextRequest): Promise<NextResponse> {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return createErrorResponse('Unauthorized', 401)
    }

    // Validate query parameters
    const { searchParams } = new URL(request.url)
    const queryResult = playlistQuerySchema.safeParse({
      include_public: searchParams.get('include_public'),
      with_tracks: searchParams.get('with_tracks'),
      limit: searchParams.get('limit'),
      offset: searchParams.get('offset')
    })

    if (!queryResult.success) {
      return createErrorResponse('Invalid query parameters', 400, queryResult.error.issues)
    }

    const { include_public: includePublic, with_tracks: withTracks, limit, offset } = queryResult.data

    // Validate and normalize pagination parameters
    const { limit: finalLimit, offset: finalOffset } = validatePaginationParams({
      limit,
      offset,
      maxLimit: 100,
      defaultLimit: 20
    })

    // Fetch playlists and total count
    let playlists: PlaylistBase[] = []
    let totalCount = 0
    let error: any = null

    if (includePublic) {
      // Get own + public playlists with counts - use separate queries to avoid SQL injection
      const [userPlaylistsResult, publicPlaylistsResult, userCountResult, publicCountResult] = await Promise.all([
        // Data queries with pagination
        supabase
          .from('playlists')
          .select('*')
          .eq('created_by', user.id)
          .is('deleted_at', null)
          .order('updated_at', { ascending: false })
          .range(finalOffset, finalOffset + finalLimit - 1),
        supabase
          .from('playlists')
          .select('*')
          .eq('is_public', true)
          .neq('created_by', user.id) // Avoid duplicates
          .is('deleted_at', null)
          .order('updated_at', { ascending: false }),
        // Count queries
        supabase
          .from('playlists')
          .select('*', { count: 'exact', head: true })
          .eq('created_by', user.id)
          .is('deleted_at', null),
        supabase
          .from('playlists')
          .select('*', { count: 'exact', head: true })
          .eq('is_public', true)
          .neq('created_by', user.id)
          .is('deleted_at', null)
      ])

      if (userPlaylistsResult.error || publicPlaylistsResult.error) {
        error = userPlaylistsResult.error || publicPlaylistsResult.error
      } else {
        const userPlaylists = userPlaylistsResult.data || []
        const publicPlaylists = publicPlaylistsResult.data || []

        // Apply pagination to combined results
        const combined = [...userPlaylists, ...publicPlaylists]
          .sort((a, b) => new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime())

        playlists = combined.slice(finalOffset, finalOffset + finalLimit)
        totalCount = (userCountResult.count || 0) + (publicCountResult.count || 0)
      }
    } else {
      // Get only own playlists with pagination and count
      const [dataResult, countResult] = await Promise.all([
        supabase
          .from('playlists')
          .select('*')
          .eq('created_by', user.id)
          .is('deleted_at', null)
          .order('updated_at', { ascending: false })
          .range(finalOffset, finalOffset + finalLimit - 1),
        supabase
          .from('playlists')
          .select('*', { count: 'exact', head: true })
          .eq('created_by', user.id)
          .is('deleted_at', null)
      ])

      playlists = dataResult.data || []
      totalCount = countResult.count || 0
      error = dataResult.error || countResult.error
    }

    if (error) {
      console.error('Database error fetching playlists:', error)
      return createErrorResponse('Failed to fetch playlists', 500)
    }

    // Enhance playlists with track count and duration data using a single aggregated query
    if (playlists && playlists.length > 0) {
      const playlistIds = playlists.map(p => p.id)

      // Get all track stats for all playlists in one query with security filtering
      const { data: trackStats } = await supabase
        .from('playlist_tracks')
        .select(`
          playlist_id,
          tracks!inner(duration_seconds, created_by, is_public)
        `)
        .in('playlist_id', playlistIds)
        .is('tracks.deleted_at', null)

      // Group track stats by playlist ID with security filtering
      interface TrackStat {
        playlist_id: string
        tracks: {
          duration_seconds: number | null
          created_by: string
          is_public: boolean
        }
      }

      const statsByPlaylist = (trackStats as TrackStat[] || []).reduce((acc, stat) => {
        const playlistId = stat.playlist_id
        const track = stat.tracks

        // Only count tracks the user has permission to access
        if (track && (track.created_by === user.id || track.is_public)) {
          if (!acc[playlistId]) {
            acc[playlistId] = { count: 0, totalDuration: 0 }
          }
          acc[playlistId].count += 1
          acc[playlistId].totalDuration += track.duration_seconds ?? 0
        }
        return acc
      }, {} as Record<string, { count: number; totalDuration: number }>)

      // If withTracks is true, fetch full track data for each playlist with proper permission checks
      let enhancedPlaylists: PlaylistVariant[]
      if (withTracks) {
        const playlistsWithTracks = await Promise.all(playlists.map(async (playlist): Promise<PlaylistWithTracks> => {
          // Permission check: Only fetch tracks if user owns playlist or playlist is public
          const canAccessTracks = playlist.created_by === user.id || playlist.is_public

          if (!canAccessTracks) {
            // Return playlist without tracks if user lacks permission
            return {
              ...playlist,
              track_count: 0,
              total_duration: 0,
              tracks: [] as PlaylistTrackWithTrack[]
            }
          }

          const { data: playlistTracks, error: playlistTracksError } = await supabase
            .from('playlist_tracks')
            .select(`
              id,
              position,
              created_at,
              track:tracks!inner(*)
            `)
            .eq('playlist_id', playlist.id)
            .is('tracks.deleted_at', null)
            .order('position', { ascending: true })

          if (playlistTracksError) {
            console.error(`Failed to fetch tracks for playlist ${playlist.id}:`, playlistTracksError)
            // Return playlist without tracks if fetch fails
            return {
              ...playlist,
              track_count: 0,
              total_duration: 0,
              tracks: [] as PlaylistTrackWithTrack[]
            }
          }

          // With inner join, all tracks should be valid
          const validTracks = playlistTracks || []

          // Additional security: Filter tracks based on ownership and privacy
          const accessibleTracks = validTracks.filter(pt => {
            const track = pt.track
            // User can access track if they own it or it's public
            return track && (track.created_by === user.id || track.is_public)
          }) as PlaylistTrackWithTrack[]

          return {
            ...playlist,
            track_count: accessibleTracks.length,
            total_duration: accessibleTracks.reduce((sum, pt) => sum + (pt.track?.duration_seconds ?? 0), 0),
            tracks: accessibleTracks
          }
        }))
        enhancedPlaylists = playlistsWithTracks
      } else {
        // Merge stats with playlists (no tracks)
        enhancedPlaylists = playlists.map((playlist): PlaylistWithStats => ({
          ...playlist,
          track_count: statsByPlaylist[playlist.id]?.count ?? 0,
          total_duration: statsByPlaylist[playlist.id]?.totalDuration ?? 0
        }))
      }

      return NextResponse.json(
        createPaginatedResponse(enhancedPlaylists, totalCount, finalLimit, finalOffset)
      )
    }

    // If no playlists, return empty response  
    return NextResponse.json(
      createPaginatedResponse([], totalCount, finalLimit, finalOffset)
    )

  } catch (error) {
    console.error('Playlists GET error:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

// POST /api/playlists - Create new playlist
export async function POST(request: NextRequest): Promise<NextResponse> {
  try {
    const supabase = await createClient()

    // Check authentication
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return createErrorResponse('Unauthorized', 401)
    }

    const body: CreatePlaylistRequest = await request.json()

    // Validate request body
    const bodyResult = createPlaylistSchema.safeParse(body)

    if (!bodyResult.success) {
      return createErrorResponse('Invalid request data', 400, bodyResult.error.issues)
    }

    const { title, description, is_public, is_collaborative } = bodyResult.data

    // Additional security validation
    if (!title || title.trim().length === 0) {
      return createErrorResponse('Title is required', 400)
    }

    // Security: Prevent excessive title/description lengths beyond schema validation
    if (title.length > 200) {
      return createErrorResponse('Title too long', 400)
    }

    if (description && description.length > 1000) {
      return createErrorResponse('Description too long', 400)
    }

    // Create playlist with validated data
    const playlistData = {
      title: title.trim(),
      description: description?.trim() || null,
      created_by: user.id,
      is_public: Boolean(is_public),
      is_collaborative: Boolean(is_collaborative),
    }

    const { data: playlist, error } = await supabase
      .from('playlists')
      .insert(playlistData)
      .select()
      .single()

    if (error) {
      console.error('Database error creating playlist:', error)
      return createErrorResponse('Failed to create playlist', 500)
    }

    // Return properly typed playlist response
    const playlistWithStats: PlaylistWithStats = {
      ...playlist,
      track_count: 0,
      total_duration: 0
    }

    return NextResponse.json({
      success: true,
      playlist: playlistWithStats
    }, { status: 201 })

  } catch (error) {
    console.error('Playlist POST error:', error)
    return createErrorResponse('Internal server error', 500)
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { b2Service } from '@/lib/storage/backblaze'
import { validateStringInput, createSecureCorsHeaders } from '@/lib/security/validation'

export async function POST(request: NextRequest) {
  try {
    const supabase = await createClient()
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 })
    }

    console.log('Starting track recovery from Backblaze...')

    // Parse request body for pagination options to prevent DoS
    const body = await request.json().catch(() => ({}))
    const maxFiles = Math.min(body.maxFiles || 100, 500) // Limit to prevent DoS
    const startAfter = body.startAfter || null

    console.log(`Recovery parameters: maxFiles=${maxFiles}, startAfter=${startAfter}`)

    // List files with pagination to prevent resource exhaustion
    const files = await b2Service.listFiles(maxFiles, startAfter)
    console.log(`Found ${files.length} files in Backblaze (batch)`)

    // Use typed arrays for better type safety
    const recoveredTracks: any[] = []
    const errors: { fileName: string; error: string }[] = []

    for (const file of files) {
      try {
        // Skip non-audio files
        if (!file.contentType?.startsWith('audio/')) {
          continue
        }

        // Extract and validate metadata from file info
        const metadata = file.fileInfo || {}
        const uploadedBy = metadata.uploaded_by

        // Skip files not uploaded by current user (for now - we'll handle this later)
        if (uploadedBy !== user.id) {
          continue
        }

        // Validate and sanitize metadata to prevent malicious data injection
        const titleValidation = validateStringInput(
          metadata.title || file.fileName.replace(/\.[^/.]+$/, ""),
          'title',
          { required: true, maxLength: 200, trim: true }
        )

        const artistValidation = validateStringInput(
          metadata.artist,
          'artist', 
          { required: false, maxLength: 100, trim: true }
        )

        if (!titleValidation.isValid) {
          errors.push({
            fileName: file.fileName,
            error: `Invalid title: ${titleValidation.error}`
          })
          continue
        }

        const trackData = {
          title: titleValidation.data,
          artist: artistValidation.data,
          description: null,
          file_name: file.fileName,
          file_size: file.size,
          file_type: file.contentType,
          storage_url: file.fileUrl,
          storage_key: file.fileName,
          file_url: file.fileUrl,
          user_id: uploadedBy,
          created_by: uploadedBy,
          is_public: false,
          genre: metadata.genre || null,
          bpm: metadata.bpm ? parseInt(metadata.bpm) : null,
          key_signature: metadata.key_signature || null,
          duration_seconds: null, // We'll need to recalculate this if needed
          folder_path: null,
          project_name: null,
          session_date: null,
          content_type: null,
          relative_path: null,
          metadata: {
            recovered_from_backblaze: true,
            original_upload_timestamp: file.uploadTimestamp,
            recovery_timestamp: new Date().toISOString()
          },
          created_at: new Date(file.uploadTimestamp).toISOString(),
          updated_at: new Date().toISOString()
        }

        // Insert track record
        const { data: track, error: insertError } = await supabase
          .from('tracks')
          .insert(trackData)
          .select()
          .single()

        if (insertError) {
          console.error(`Failed to insert track ${file.fileName}:`, insertError)
          errors.push({
            fileName: file.fileName,
            error: insertError.message
          })
        } else {
          recoveredTracks.push(track)
          console.log(`Recovered track: ${trackData.title}`)
        }

      } catch (fileError) {
        console.error(`Error processing file ${file.fileName}:`, fileError)
        errors.push({
          fileName: file.fileName,
          error: fileError instanceof Error ? fileError.message : String(fileError)
        })
      }
    }

    console.log(`Recovery complete: ${recoveredTracks.length} tracks recovered, ${errors.length} errors`)

    // Return summary to avoid overwhelming client with large datasets
    const hasMoreFiles = files.length === maxFiles // Indicates there might be more files
    const lastFileName = files.length > 0 ? files[files.length - 1].fileName : null

    return NextResponse.json({
      success: true,
      recovered: recoveredTracks.length,
      errors: errors.length,
      hasMoreFiles,
      nextStartAfter: hasMoreFiles ? lastFileName : null,
      summary: {
        recoveredTracks: recoveredTracks.slice(0, 10).map(t => ({ 
          id: t.id, 
          title: t.title, 
          artist: t.artist 
        })), // Limit to first 10 for display
        recentErrors: errors.slice(0, 5), // Limit errors shown
        totalProcessed: files.length
      }
    }, {
      headers: createSecureCorsHeaders()
    })

  } catch (error) {
    console.error('Recovery error:', error)
    // Don't leak internal error details to client
    return NextResponse.json({ 
      error: 'Recovery operation failed' 
    }, { 
      status: 500,
      headers: createSecureCorsHeaders()
    })
  }
}

import { NextRequest, NextResponse } from 'next/server'
import { createClient } from '@/lib/supabase/server'
import { b2Service } from '@/lib/storage/backblaze'
import { sanitizeStorageKey, createSecureCorsHeaders } from '@/lib/security/validation'

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
): Promise<NextResponse> {
  try {
    const { id: trackId } = await params

    // Check authentication
    const supabase = await createClient()
    const { data: { user }, error: authError } = await supabase.auth.getUser()

    if (authError || !user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      )
    }

    // Get track information
    const { data: track, error: trackError } = await supabase
      .from('tracks')
      .select('*')
      .eq('id', trackId)
      .eq('created_by', user.id)
      .is('deleted_at', null)
      .single()

    if (trackError || !track) {
      return NextResponse.json(
        { error: 'Track not found' },
        { status: 404 }
      )
    }

    // Access check is now handled by the query with created_by filter

    // Extract and sanitize filename from storage_key to prevent directory traversal
    let fileName: string
    try {
      fileName = sanitizeStorageKey(track.storage_key)
    } catch (sanitizationError) {
      console.error('Storage key sanitization failed:', sanitizationError)
      return NextResponse.json(
        { error: 'Invalid file path' },
        { status: 400 }
      )
    }

    console.log('Download API Debug:', {
      trackId,
      fileName,
      storage_url: track.storage_url,
      track_title: track.title
    })

    try {
      // Get authorized download URL from Backblaze B2
      const authorizedUrl = await b2Service.getAuthorizedDownloadUrl(fileName)

      console.log('Generated authorized URL:', authorizedUrl)

      return NextResponse.json({
        success: true,
        downloadUrl: authorizedUrl,
        expiresIn: 3600, // 1 hour
      }, {
        headers: createSecureCorsHeaders()
      })
    } catch (error) {
      console.error('B2 Service Error:', error)
      // Don't leak B2 service details to client
      return NextResponse.json(
        { error: 'Download service temporarily unavailable' },
        { 
          status: 503,
          headers: createSecureCorsHeaders()
        }
      )
    }

  } catch (error) {
    console.error('Download URL generation error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

