  const handleSavePlaylist = async (playlistData: {
    title: string
    description?: string
    is_public: boolean
    is_collaborative: boolean
  }) => {
    try {
      if (editingPlaylist) {
        // Update existing playlist
        const response = await fetch(`/api/playlists/${editingPlaylist.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(playlistData)
        })

        if (!response.ok) {
          const error = await response.json()
          throw new Error(error.error || 'Failed to update playlist')
        }

        // Update selected playlist if we're editing it
        if (selectedPlaylist && selectedPlaylist.id === editingPlaylist.id) {
          setSelectedPlaylist({ ...selectedPlaylist, ...playlistData })
        }
      } else {
        // Create new playlist
        const response = await fetch('/api/playlists', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(playlistData)
        })

        if (!response.ok) {
          const error = await response.json()
          throw new Error(error.error || 'Failed to create playlist')
        }
      }

      // Success - the PlaylistsView will refresh automatically
    } catch (err) {
      console.error('Save playlist error:', err)
      throw err
    }
  }

  const handleOpenPlaylist = (playlist: any) => {
    setSelectedPlaylist(playlist)
    setCurrentView('playlist-details')
  }

  const handleBackToPlaylists = () => {
    setSelectedPlaylist(null)
    setCurrentView('playlists')
  }

  const handleEditPlaylistFromDetails = (playlist: any) => {
    setEditingPlaylist(playlist)
    setPlaylistModalOpen(true)
  }

