                You have {trackCount} track{trackCount !== 1 ? 's' : ''} in your library. 
                <button 
                  onClick={() => setCurrentView('tracks')}
                  className="text-blue-600 hover:text-blue-800 ml-1 underline"
                >
                  View all tracks
                </button>
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  )

  const renderTracks = () => (
    <SearchableTracksView 
      key={tracksRefreshKey}
      onTrackDeleted={handleUploadComplete}
      onTrackCountChange={handleTrackCountChange}
    />
  )

  const renderPlaceholder = () => (
