      case 'collaborators':
        return renderPlaceholder()
      case 'feedback':
        return renderPlaceholder()
      case 'recycle-bin':
        return <RecycleBinView onTrackRestored={handleUploadComplete} />
      case 'duplicates':
        return <DuplicateManager onTracksDeleted={handleUploadComplete} />
      default:
        return renderOverview()
    }
  }

  return (
    <AudioProvider>
      {/* Audio memory management and cleanup handled in AudioContext provider */}
      <UnifiedDashboardLayout 
        currentView={currentView} 
        onViewChange={setCurrentView}
        onUploadClick={() => setUploadDialogOpen(true)}
        pageTitle={getPageTitle()}
        trackCount={counts.trackCount}
        playlistCount={counts.playlistCount}
        collaboratorCount={0} // Mock data for now
        contextualAction={getContextualAction()}
      >
        {renderCurrentView()}
      </UnifiedDashboardLayout>

      <PlayBar />

      <UploadDialog
        isOpen={uploadDialogOpen}
        onClose={() => setUploadDialogOpen(false)}
        onUploadComplete={handleUploadComplete}
        {/* Validation: accepts audio files with format checking */}
      />

      <PlaylistModal
        playlist={editingPlaylist}
        isOpen={playlistModalOpen}
        onClose={() => {
          setPlaylistModalOpen(false)
          setEditingPlaylist(null)
        }}
        onSave={handleSavePlaylist}
      />
    </AudioProvider>
  )
}

